var bgEl=config.bgEl,closeBtnEl=config.closeBtnEl,popupEl=config.popupEl;function hidePopUp(){bgEl.style.display="none",popupEl.className="popupEl",document.body.style.overflow="auto"}function clearBody(){bgEl.remove(),popupEl.remove(),document.querySelector("#itmStyles").remove()}function dismissOnOverlayClick(){addEvent(bgEl,"click",()=>{hidePopUp(),setTimeout(()=>{clearBody()},1e3)})}function dismissOnContentClick(){addEvent(popupEl,"click",()=>{hidePopUp(),setTimeout(()=>{clearBody()},1e3)})}function dismissOnCloseBtnClick(){addEvent(closeBtnEl,"click",()=>{hidePopUp(),setTimeout(()=>{clearBody()},1e3)})}function dismissAutoClose(){addEvent(bgEl,"mousemove",()=>{setTimeout(()=>{hidePopUp(),setTimeout(()=>{clearBody()},800)},1e3*config.generalAutoCloseDelay)})}function dismissOnEscClick(){addEvent(document.querySelector("body"),"keydown",e=>{"Escape"===e.key&&(hidePopUp(),setTimeout(()=>{clearBody()},1e3))})}